





ALTER TABLE tb_capitulo ALTER COLUMN modulo_id TYPE BIGINT;

--INSERT INTO tb_capitulo (titulo, url_imagem, body, url_video, modulo_id) VALUES ('Quais ideias são bem-vindas', '', 'A resposta é muito simples! Qualquer ideia que desejar, pense de maneira inovadora, nossos funcionários ficarão muito felizes em poder avaliar suas ideias e lhe dar o que voê merece.',  '', 3);
--INSERT INTO tb_capitulo (titulo, url_imagem, body, url_video, modulo_id) VALUES ('Suas premiações.', '', 'Mas oque seria isso?\n\n Se comprovado impacto relevante (financeiro, produtividade, sustentabilidade), o colaborador recebe premiação em eventos especiais, conduzidos pelo CEO Global e alta liderança, apresentando sua solução à diretoria e Grupo Executivo. Ideias que geram ganho financeiro superior a R$ 1 milhão podem render ao proponente até 5% do valor economizado, limitado a R$ 120 mil.',  '', 3);
--INSERT INTO tb_capitulo (titulo, url_imagem, body, url_video, modulo_id) VALUES ('Por que Participar', '', 'O CLIC representa uma oportunidade real para cada colaborador transformar sua visão em ação, colaborar ativamente para a evolução da Eurofarma e ser reconhecido por seu papel inovador. Compartilhe sua ideia e faça parte do movimento que impulsiona resultados, inspira pessoas e molda o futuro da Eurofarma!',  '', 4);
--INSERT INTO tb_capitulo (titulo, url_imagem, body, url_video, modulo_id) VALUES ('Gestão e Comunicão', '' , 'O programa é gerido por uma equipe dedicada do Six Sigma, responsável pelo acompanhamento de ideias, resultados, comunicação de status e articulação com o Comitê CLIC. As comunicações são realizadas por canais estratégicos: intranet, TV corporativa, Teams, revista Panorama, Engage (rede social interna), além da produção de vídeos de reconhecimento—ampliando o engajamento e a cultura de valorização das ideias.',  '', 4);








